mapper.select:
SELECT
-- 级联表的字段 ,feijipiao.feijipiao_name as feijipiaoName ,feijipiao.feiji_types as feijiTypes ,feijipiao.feijipiao_xinghao_types as feijipiaoXinghaoTypes ,feijipiao.feijipiao_chufadi as feijipiaoChufadi ,feijipiao.feijipiao_mudidi as feijipiaoMudidi ,feijipiao.feijipiao_photo as feijipiaoPhoto ,feijipiao.feijipiao_time as feijipiaoTime ,feijipiao.feijipiao_new_money as feijipiaoNewMoney ,feijipiao.feijipiao_content as feijipiaoContent ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM feijipiao_collection a left JOIN feijipiao feijipiao ON a.feijipiao_id = feijipiao.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.feijipiao_id = #{params.feijipiaoId}
and a.yonghu_id = #{params.yonghuId}
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and feijipiao.feijipiao_name like CONCAT('%',#{params.feijipiaoName},'%')
and feijipiao.feiji_types = #{params.feijiTypes}
and feijipiao.feijipiao_xinghao_types = #{params.feijipiaoXinghaoTypes}
and feijipiao.feijipiao_chufadi like CONCAT('%',#{params.feijipiaoChufadi},'%')
and feijipiao.feijipiao_mudidi like CONCAT('%',#{params.feijipiaoMudidi},'%')
and feijipiao.feijipiao_time like CONCAT('%',#{params.feijipiaoTime},'%')
and feijipiao.feijipiao_new_money >= #{params.feijipiaoNewMoneyStart}
and feijipiao.feijipiao_new_money <= #{params.feijipiaoNewMoneyEnd}
and feijipiao.feijipiao_content like CONCAT('%',#{params.feijipiaoContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
