mapper.select:
SELECT
-- 级联表的字段 ,huochepiao.huochepiao_name as huochepiaoName ,huochepiao.huochepiao_types as huochepiaoTypes ,huochepiao.huochepiao_xinghao_types as huochepiaoXinghaoTypes ,huochepiao.huochepiao_chufadi as huochepiaoChufadi ,huochepiao.huochepiao_mudidi as huochepiaoMudidi ,huochepiao.huochepiao_photo as huochepiaoPhoto ,huochepiao.huochepiao_time as huochepiaoTime ,huochepiao.huochepiao_new_money as huochepiaoNewMoney ,huochepiao.huochepiao_content as huochepiaoContent ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM huochepiao_collection a left JOIN huochepiao huochepiao ON a.huochepiao_id = huochepiao.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.huochepiao_id = #{params.huochepiaoId}
and a.yonghu_id = #{params.yonghuId}
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and huochepiao.huochepiao_name like CONCAT('%',#{params.huochepiaoName},'%')
and huochepiao.huochepiao_types = #{params.huochepiaoTypes}
and huochepiao.huochepiao_xinghao_types = #{params.huochepiaoXinghaoTypes}
and huochepiao.huochepiao_chufadi like CONCAT('%',#{params.huochepiaoChufadi},'%')
and huochepiao.huochepiao_mudidi like CONCAT('%',#{params.huochepiaoMudidi},'%')
and huochepiao.huochepiao_time like CONCAT('%',#{params.huochepiaoTime},'%')
and huochepiao.huochepiao_new_money >= #{params.huochepiaoNewMoneyStart}
and huochepiao.huochepiao_new_money <= #{params.huochepiaoNewMoneyEnd}
and huochepiao.huochepiao_content like CONCAT('%',#{params.huochepiaoContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
